tinymce.PluginManager.add('ex_loadfile', function (editor, url) {
    editor.addButton('ex_loadfile', {
        icon: 'browse',
        tooltip: "Thêm tệp tin",
        onclick: function () {
            editor.windowManager.open({
                title: editor.settings.ex_loadfile.title,
                file: editor.settings.ex_loadfile.filepath,
                width: window.innerWidth-50,
                height: window.innerHeight-100,
                inline: 1,
                resizable: !0,
                maximizable: !0
            }, {
                editor: editor
            });
        }

    });
});